import React, { useEffect } from 'react';
import '../styles/reset-tool.css';
import '../styles/style.css';
import '../styles/square.css';
import '../styles/neon.css';
import '../styles/animation-article.css';

function Content({ onContentChange }) {
  useEffect(() => {
    const neonBorders = document.querySelectorAll('.neon-border');
    const switchArticle1 = document.getElementById('switch-article1');
    const switchArticle2 = document.getElementById('switch-article2');
    const switchArticle3 = document.getElementById('switch-article3');
    const switchButtons = document.getElementsByClassName('down-arrow');
    const articles = document.querySelectorAll('.animated-article');

    function getRandomHSL() {
      const hue = Math.floor(Math.random() * 360);
      return `hsl(${hue}, 100%, 50%)`;
    }

    function changeNeonColors() {
      neonBorders.forEach(border => {
        let currentHue = parseInt(border.dataset.hue, 10);
        currentHue = (currentHue + 1) % 360;
        const newColor = `hsl(${currentHue}, 100%, 50%)`;

        border.style.setProperty('--neon-color-1', newColor);
        border.style.setProperty('--neon-color-2', newColor);
        border.style.borderColor = newColor;
        border.style.boxShadow = `0 0 10px ${newColor}, 0 0 20px ${newColor}, 0 0 30px ${newColor}`;

        border.dataset.hue = currentHue;
      });
    }

    neonBorders.forEach(border => {
      const color = getRandomHSL();
      border.style.borderColor = color;
      border.style.boxShadow = `0 0 10px ${color}, 0 0 20px ${color}, 0 0 30px ${color}`;
      border.dataset.hue = parseInt(color.match(/\d+/)[0], 10);
    });

    const colorInterval = setInterval(changeNeonColors, 50);

    function toggleArticleContent(article) {
      const dataContent = article.getAttribute('data-content');
      const h2 = article.querySelector('h2');
      const p = article.querySelector('p');

      switch (dataContent) {
        case 'biographie':
          h2.textContent = 'Mes compétences';
          p.textContent = 'Description de l\'article 4.';
          article.setAttribute('data-content', 'competence');
          break;
        case 'competence':
          h2.textContent = 'Mes projets';
          p.textContent = 'Description de l\'article 6.';
          article.setAttribute('data-content', 'project');
          break;
        case 'project':
          h2.textContent = 'Qui suis-je ?';
          p.textContent = 'Découvrez qui je suis, ma personalité, mon github, mon numéro de telephone, ect... en cliquant sur cette article';
          article.setAttribute('data-content', 'biographie');
          break;
        case 'etude':
          h2.textContent = 'Mon Parcours scolaire';
          p.textContent = 'Description de l\'article 5.';
          article.setAttribute('data-content', 'parcours');
          break;
        case 'parcours':
          h2.textContent = 'Que fais-je ?';
          p.textContent = 'Description de l\'article 2.';
          article.setAttribute('data-content', 'etude');
          break;
        default:
          break;
      }
    }

    Array.from(switchButtons).forEach(button => {
      button.addEventListener('click', async (event) => {
        const clickedButton = event.target;
        let article;

        if (clickedButton === switchArticle1) {
          article = document.querySelector('.article1');
        } else if (clickedButton === switchArticle2) {
          article = document.querySelector('.article2');
        } else if (clickedButton === switchArticle3) {
          article = document.querySelector('.article3');
        }

        if (article) {
          article.classList.add('article-hidden');
          setTimeout(() => {
            toggleArticleContent(article);
            article.classList.remove('article-hidden');
          }, 500);
        }
      });
    });

    articles.forEach(article => {
      article.addEventListener('click', async () => {
        const contentType = article.getAttribute('data-content');
        const response = await fetch(`http://localhost:5000/api/${contentType}`);
        const fetchedData = await response.json();
        onContentChange(contentType, fetchedData);
      });
    });

    articles.forEach(article => {
      article.addEventListener('mouseenter', () => {
        article.classList.add('hover');
      });

      article.addEventListener('mouseleave', () => {
        article.classList.remove('hover');
      });
    });

    return () => {
      clearInterval(colorInterval);
      Array.from(switchButtons).forEach(button => button.removeEventListener('click', () => {}));
      articles.forEach(article => article.removeEventListener('click', () => {}));
      articles.forEach(article => article.removeEventListener('mouseenter', () => {}));
      articles.forEach(article => article.removeEventListener('mouseleave', () => {}));
    };
  }, [onContentChange]);

  return (
    <main>
      <section className="intro-section text-center py-5">
        <h2 className="text-4xl mb-4">Bienvenue sur mon e-portfolio</h2>
        <p className="text-gray-400">Découvrez mon parcours, mes compétences et mes projets.</p>
      </section>

      <section className="my-8">
        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          <div className="article-container">
            <div className="bg-white p-4 rounded shadow-md neon-border animated-article article1 h-full" data-content="biographie">
              <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Qui suis-je ?</h2>
              <div className="article-content">
                <p className="text-gray-600">Description de l'article 1.</p>
              </div>
            </div>
            <button id="switch-article1" className="down-arrow">▼</button>
          </div>
          
          <div className="article-container">
            <div className="bg-white p-4 rounded shadow-md neon-border animated-article article2 h-full" data-content="etude">
              <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Que fais-je ?</h2>
              <div className="article-content">
                <p className="text-gray-600">Description de l'article 2.</p>
              </div>
            </div>
            <button id="switch-article2" className="down-arrow">▼</button>
          </div>
          
          <div className="article-container">
            <div className="bg-white p-4 rounded shadow-md neon-border animated-article article3 h-full" data-content="futur">
              <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Qui je veux être ?</h2>
              <div className="article-content">
                <p className="text-gray-600">Description de l'article 3.</p>
              </div>
            </div>
            <button id="switch-article3" className="down-arrow">▼</button>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Content;
