import React, { useState } from 'react';


import './styles/reset-tool.css';
import './styles/style.css';
import './styles/square.css';
import './styles/neon.css';
import './styles/animation-article.css';

// Import des fichiers JavaScript
import './scripts/app.js';
import './scripts/app.js'; 
import './scripts/header.js';
import './scripts/main.js';

import Header from './components/Header';
import Content from './components/Content';
import Footer from './components/Footer';
import ContactForm from './components/ContactForm'; // Importer le composant ContactForm

function App() {
  const [page, setPage] = useState('accueil'); // Initialiser à "accueil"
  const [key, setKey] = useState(0); // Clé aléatoire pour forcer le rechargement

  const updatePage = (newPage) => {
    setPage(newPage);
    setKey((prevKey) => prevKey + 1); // Incrémente la clé pour forcer le rechargement
  };

  return (
    <div className="bg-dark text-light" key={key}>
      <div className='square'></div> {/* Ajout de la div square */}
      <Header updateVariable={updatePage} />
      {/* Condition pour afficher Content ou ContactForm */}
      {page === 'accueil' ? (
        <Content />
      ) : (
        <ContactForm />
      )}
      <Footer />
    </div>
  );
}

export default App;
