import React, { useState, useEffect } from 'react';
import '../styles/reset-tool.css';
import '../styles/style.css';
import '../styles/square.css';
import '../styles/neon.css';
import '../styles/animation-article.css';

function Content() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/articles');
        const data = await response.json();
        setArticles(data);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchData();
  }, []);

  const toggleArticleContent = (article) => {
    switch (article.content) {
      case 'biographie':
        article.title = "Mes compétences";
        article.description = "Description de l'article 4.";
        article.content = 'competence';
        break;
      case 'competence':
        article.title = "Mes projets";
        article.description = "Description de l'article 6.";
        article.content = 'project';
        break;
      case 'project':
        article.title = "Qui suis-je ?";
        article.description = "Description de l'article 1.";
        article.content = 'biographie';
        break;
      case 'etude':
        article.title = "Mon Parcours scolaire";
        article.description = "Description de l'article 5.";
        article.content = 'parcours';
        break;
      case 'parcours':
        article.title = "Que fais-je ?";
        article.description = "Description de l'article 2.";
        article.content = 'etude';
        break;
      default:
        break;
    }
  };

  const handleButtonClick = (event, index) => {
    const clickedArticle = articles[index];
    if (clickedArticle) {
      clickedArticle.classList.add('article-hidden');
      setTimeout(() => {
        toggleArticleContent(clickedArticle);
        clickedArticle.classList.remove('article-hidden');
      }, 500);
    }
  };

  const handleArticleClick = async (content) => {
    const square = document.querySelector('.square');
    square.classList.add('show');
    square.innerHTML = await getContentHTML(content);
  };

  const handleMouseEnter = (article) => {
    article.classList.add('hover');
  };

  const handleMouseLeave = (article) => {
    article.classList.remove('hover');
  };

  const handleCloseButtonClick = () => {
    const square = document.querySelector('.square');
    square.classList.remove('show');
    square.innerHTML = '';
  };

  const getContentHTML = async (content) => {
    try {
      const response = await fetch(`http://localhost:5000/api/${content}`);
      const data = await response.json();

      switch (content) {
        case 'personal-info':
          const { name, email, phone, github } = data;
          return `
            <button id="closeButton">&#x1F5D9;</button>
            <section class="intro-section text-center py-5">
              <h1>${name}</h1>
            </section>
            <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-1 gap-4 mt-4 -mx-4">
              <div class="rounded bg-white p-4 col-span-6 rounded shadow-md text-dark article-container">  
                <p>Email: ${email}</p><br />
                <p>Téléphone: ${phone}</p><br /> 
                <p>Github: <a href="${github}" target="_blank">${github}</a></p>
              </div>
            </div>`;
        case 'skills':
          return `
            <button id="closeButton">&#x1F5D9;</button>
            <section class="intro-section text-center py-5">
              <h1>Compétences</h1>
            </section>
            <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-1 gap-4 mt-4 -mx-4">
              <div class="rounded bg-white p-4 col-span-6 rounded shadow-md text-dark article-container">
                <ul>${data.map(skill => `<li>${skill}</li>`).join('')}</ul>
              </div>
            </div>`;
        case 'projects':
          return `
            <button id="closeButton">&#x1F5D9;</button>
            <section class="intro-section text-center py-5">
              <h1>Projets</h1>
            </section>
            <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-1 gap-4 mt-4 -mx-4 project">
              <div class="rounded bg-white p-4 col-span-6 rounded shadow-md text-dark article-container">
                ${data.map(project => `
                  <div class="project">
                    <h3>${project.title}</h3>
                    <p>${project.description}</p>
                    ${project.github ? `<p><a href="${project.github}" target="_blank">GitHub</a></p>` : ''}
                  </div>
                `).join('')}
              </div>
            </div>`;
        default:
          return '';
      }
    } catch (error) {
      console.error(`Error fetching ${content}:`, error);
      return '<p>Erreur de chargement des données</p>';
    }
  };

  return (
    <main>
      <section className="intro-section text-center py-5">
        <h2 className="text-4xl mb-4">Bienvenue sur mon e-portfolio</h2>
        <p className="text-gray-400">Découvrez mon parcours, mes compétences et mes projets.</p>
      </section>
      
      <section className="my-8">
        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          {articles.map((article, index) => (
            <div key={index} className={`bg-white p-4 rounded shadow-md neon-border animated-article article${index + 1}`}
                 data-content={article.content}
                 onClick={() => handleArticleClick(article.content)}
                 onMouseEnter={() => handleMouseEnter(article)}
                 onMouseLeave={() => handleMouseLeave(article)}>
              <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">{article.title}</h2>
              <div className="article-content">
                <p className="text-gray-600">{article.description}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          {articles.map((_, index) => (
            <button key={index} className="down-arrow" onClick={(event) => handleButtonClick(event, index)}>▼</button>
          ))}
        </div>
      </section>

      <div className="square"></div>
    </main>
  );
}

export default Content;
