import React, { useEffect } from 'react';
import { Link } from "react-router-dom";
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/Header.css';
function Header() {
  useEffect(() => {
    const lightModeBtn = document.getElementById('light-mode-btn');
    const darkModeBtn = document.getElementById('dark-mode-btn');
    const body = document.body;
    const hamburgerMenu = document.querySelector('.first-button');

    const handleLightMode = () => {
      body.classList.remove('bg-dark', 'text-light');
      hamburgerMenu.classList.remove('light-mode');
      body.classList.add('bg-light', 'text-dark');
      hamburgerMenu.classList.add('dark-mode');
    };

    const handleDarkMode = () => {
      body.classList.remove('bg-light', 'text-dark');
      hamburgerMenu.classList.remove('dark-mode');
      body.classList.add('bg-dark', 'text-light');
      hamburgerMenu.classList.add('light-mode');
    };

    lightModeBtn.addEventListener('click', handleLightMode);
    darkModeBtn.addEventListener('click', handleDarkMode);

    document.querySelector('.first-button').addEventListener('click', () => {
      document.querySelector('.animated-icon1').classList.toggle('open');
    });

    return () => {
      lightModeBtn.removeEventListener('click', handleLightMode);
      darkModeBtn.removeEventListener('click', handleDarkMode);
    };
  }, []);

  return (
    <header>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <button className="navbar-toggler first-button" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <div id="hamburger" className="animated-icon1 light-mode"><span></span><span></span><span></span></div>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  <i className="fas fa-cog" title="Paramètre"></i>
                </a>
                <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                  <li><a className="dropdown-item" id="light-mode-btn" href="#">Light mode</a></li>
                  <li><a className="dropdown-item" id="dark-mode-btn" href="#">Dark mode</a></li>
                </ul>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/">
                  <i className="fas fa-home" title="Accueil"></i>
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/ContactMe">
                  <i className="fas fa-envelope" title="Contact"></i>
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}

export default Header;
