import React, { useState } from 'react';
import Header from './components/Header';
import Content from './components/Content';
import Footer from './components/Footer';
import ContactForm from './components/ContactForm'; // Importer le composant ContactForm

function App() {
  const [page, setPage] = useState('accueil'); // Initialiser à "accueil"

  const updatePage = (newPage) => {
    setPage(newPage);
  };

  // Fonction pour recharger la page
  const reloadPage = () => {
    window.location.reload();
  };

  return (
    <div className="bg-dark text-light">
      <Header updateVariable={updatePage} />
      {/* Condition pour afficher Content ou ContactForm */}
      {page === 'accueil' ? (
        <Content />
      ) : (
        <ContactForm reloadPage={reloadPage} />
      )}
      <Footer />
    </div>
  );
}

export default App;
