import React, { useState, useEffect } from 'react';
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

import '../styles/reset-tool.css';
import '../styles/style.css';
import '../styles/square.css';
import '../styles/neon.css';
import '../styles/animation-article.css';

// Composant pour afficher les informations personnelles
const PersonalInfo = ({ data }) => (
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-1 gap-4 mt-4 -mx-4">
    <div className="rounded bg-white p-4 col-span-6 rounded shadow-md text-dark article-container">
      <p>Email: {data.email}</p><br />
      <p>Téléphone: {data.phone}</p><br />
      <p>Github: <a href={data.github} target="_blank" rel="noopener noreferrer">{data.github}</a></p>
    </div>
  </div>
);

// Composant pour afficher les compétences
const Skills = ({ data }) => (
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-1 gap-4 mt-4 -mx-4">
    <div className="rounded bg-white p-4 col-span-6 rounded shadow-md text-dark article-container">
      <ul>{data.map(skill => <li key={skill}>{skill}</li>)}</ul>
    </div>
  </div>
);

// Composant pour afficher les projets
const Projects = ({ data }) => (
  <Carousel autoPlay infiniteLoop interval={3000} showThumbs={false} showStatus={false}>
    {data.map((project, index) => (
      <div key={index}>
        <h2>{project.title}</h2>
        <p>{project.description}</p>
        {project.github && (
          <a href={project.github} target="_blank" rel="noopener noreferrer">
            Voir sur GitHub
          </a>
        )}
      </div>
    ))}
  </Carousel>
);

function Content() {
  const [content, setContent] = useState('biographie');
  const [data, setData] = useState(null);

  useEffect(() => {
    async function fetchData(contentType) {
      let url;
      switch (contentType) {
        case 'biographie':
          url = 'http://localhost:5000/api/personal-info';
          break;
        case 'competence':
          url = 'http://localhost:5000/api/skills';
          break;
        case 'project':
          url = 'http://localhost:5000/api/projects';
          break;
        default:
          return;
      }
      try {
        const response = await fetch(url);
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error(`Error fetching ${contentType}:`, error);
        setData(null);
      }
    }

    fetchData(content);
  }, [content]);

  const handleContentChange = (newContent) => {
    setContent(newContent);
  };

  const renderContent = () => {
    switch (content) {
      case 'biographie':
        return data ? <PersonalInfo data={data} /> : <p>Loading...</p>;
      case 'competence':
        return data ? <Skills data={data} /> : <p>Loading...</p>;
      case 'project':
        return data ? <Projects data={data} /> : <p>Loading...</p>;
      default:
        return null;
    }
  };

  return (
    <main>
      <section className="intro-section text-center py-5">
        <h2 className="text-4xl mb-4">Bienvenue sur mon e-portfolio</h2>
        <p className="text-gray-400">Découvrez mon parcours, mes compétences et mes projets.</p>
      </section>
      
      <section className="my-8">
        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          <div className="bg-white p-4 rounded shadow-md neon-border animated-article h-full" onClick={() => handleContentChange('biographie')}>
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Qui suis-je ?</h2>
            <p className="text-gray-600">Description de l'article 1.</p>
          </div>
          <div className="bg-white p-4 rounded shadow-md neon-border animated-article h-full" onClick={() => handleContentChange('competence')}>
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Que fais-je ?</h2>
            <p className="text-gray-600">Description de l'article 2.</p>
          </div>
          <div className="bg-white p-4 rounded shadow-md neon-border animated-article h-full" onClick={() => handleContentChange('project')}>
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Mes projets</h2>
            <p className="text-gray-600">Description de l'article 3.</p>
          </div>
        </div>
      </section>

      <section>
        {renderContent()}
      </section>
    </main>
  );
}

export default Content;
