import React from 'react';
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const ArticleContent = ({ type, data }) => {
  switch (type) {
    case 'biographie':
      return (
        <div>
          <h1>{data.name}</h1>
          <p>Email: {data.email}</p>
          <p>Téléphone: {data.phone}</p>
          <p>Github: <a href={data.github} target="_blank" rel="noopener noreferrer">{data.github}</a></p>
        </div>
      );

    case 'competence':
      return (
        <div>
          <h1>Compétences</h1>
          <ul>{data.skills.map((skill, index) => <li key={index}>{skill}</li>)}</ul>
        </div>
      );

    case 'project':
      return (
        <div>
          <h1>Mes projets</h1>
          {/* Remplacez le texte par le composant Carousel avec vos projets */}
          <Carousel autoPlay infiniteLoop interval={3000} showThumbs={false} showStatus={false}>
            {data.projects.map((project, index) => (
              <div key={index}>
                <img src={project.image} alt={project.title} />
                <p className="legend">{project.title}</p>
              </div>
            ))}
          </Carousel>
        </div>
      );

    default:
      return null;
  }
};

export default ArticleContent;
