import React, { useState, useEffect } from 'react';
import Biographie from './Biographie';
import Competence from './Competence';
import Project from './Project';
import '../styles/reset-tool.css';
import '../styles/style.css';
import '../styles/square.css';
import '../styles/neon.css';
import '../styles/animation-article.css';

function Content() {
  const [currentContent, setCurrentContent] = useState(null);
  const [data, setData] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const [personalInfoRes, skillsRes, projectsRes] = await Promise.all([
          fetch('http://localhost:5000/api/personal-info'),
          fetch('http://localhost:5000/api/skills'),
          fetch('http://localhost:5000/api/projects')
        ]);

        const [personalInfo, skills, projects] = await Promise.all([
          personalInfoRes.json(),
          skillsRes.json(),
          projectsRes.json()
        ]);

        setData({ personalInfo, skills, projects });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();

    // Gérer les couleurs néon
    const neonBorders = document.querySelectorAll('.neon-border');
    function getRandomHSL() {
      const hue = Math.floor(Math.random() * 360);
      return `hsl(${hue}, 100%, 50%)`;
    }

    function changeNeonColors() {
      neonBorders.forEach(border => {
        let currentHue = parseInt(border.dataset.hue, 10);
        currentHue = (currentHue + 1) % 360;
        const newColor = `hsl(${currentHue}, 100%, 50%)`;

        border.style.setProperty('--neon-color-1', newColor);
        border.style.setProperty('--neon-color-2', newColor);
        border.style.borderColor = newColor;
        border.style.boxShadow = `0 0 10px ${newColor}, 0 0 20px ${newColor}, 0 0 30px ${newColor}`;

        border.dataset.hue = currentHue;
      });
    }

    neonBorders.forEach(border => {
      const color = getRandomHSL();
      border.style.borderColor = color;
      border.style.boxShadow = `0 0 10px ${color}, 0 0 20px ${color}, 0 0 30px ${color}`;
      border.dataset.hue = parseInt(color.match(/\d+/)[0], 10);
    });

    const intervalId = setInterval(changeNeonColors, 50);
    return () => clearInterval(intervalId);

  }, []);

  const handleArticleClick = (contentType) => {
    setCurrentContent(contentType);
    document.querySelector('.square').classList.add('show');
  };

  const closeSquare = () => {
    setCurrentContent(null);
    document.querySelector('.square').classList.remove('show');
  };

  const renderContent = () => {
    if (!data) return null;

    switch (currentContent) {
      case 'biographie':
        return <Biographie data={data.personalInfo} />;
      case 'competence':
        return <Competence skills={data.skills} />;
      case 'project':
        return <Project projects={data.projects} />;
      default:
        return null;
    }
  };

  return (
    <main>
      <section className="intro-section text-center py-5">
        <h2 className="text-4xl mb-4">Bienvenue sur mon e-portfolio</h2>
        <p className="text-gray-400">Découvrez mon parcours, mes compétences et mes projets.</p>
      </section>
      
      <section className="my-8">
        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          <div
            className="bg-white p-4 rounded shadow-md neon-border animated-article article1 h-full"
            data-content="biographie"
            onClick={() => handleArticleClick('biographie')}
          >
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Qui suis-je ?</h2>
            <div className="article-content">
              <p className="text-gray-600">Description de l'article 1.</p>
            </div>
          </div>
          <div
            className="bg-white p-4 rounded shadow-md neon-border animated-article article2 h-full"
            data-content="competence"
            onClick={() => handleArticleClick('competence')}
          >
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Que fais-je ?</h2>
            <div className="article-content">
              <p className="text-gray-600">Description de l'article 2.</p>
            </div>
          </div>
          <div
            className="bg-white p-4 rounded shadow-md neon-border animated-article article3 h-full"
            data-content="project"
            onClick={() => handleArticleClick('project')}
          >
            <h2 className="text-xl font-semibold text-gray-800 mb-2 titre">Qui je veux être ?</h2>
            <div className="article-content">
              <p className="text-gray-600">Description de l'article 3.</p>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-3 mt-3 -mx-3">
          <button id="switch-article1" className="down-arrow" onClick={() => handleArticleClick('biographie')}>▼</button>
          <button id="switch-article2" className="down-arrow" onClick={() => handleArticleClick('competence')}>▼</button>
          <button id="switch-article3" className="down-arrow" onClick={() => handleArticleClick('project')}>▼</button>
        </div>
      </section>

      <div className="square">
        {renderContent()}
        <button id="closeButton" onClick={closeSquare}>&#x1F5D9;</button>
      </div>
    </main>
  );
}

export default Content;
