import React, { useState } from 'react';
import Header from './components/Header';
import Content from './components/Content';
import ContactForm from './components/ContactForm';

import './styles/reset-tool.css';
import './styles/style.css';
import './styles/square.css';
import './styles/neon.css';
import './styles/animation-article.css';

import './scripts/app.js'; // Assurez-vous que ces fichiers sont correctement définis et inclus
import './scripts/header.js';
import './scripts/main.js';

function App() {
  const [currentPage, setCurrentPage] = useState('accueil');

  const handleNavItemClick = (pageName) => {
    setCurrentPage(pageName);
  };

  return (
    <div className="bg-dark text-light">
      <div className="square"></div>
      <Header onNavItemClick={handleNavItemClick} />
      {currentPage === 'accueil' && <Content />}
      {currentPage === 'contact' && <ContactForm />}
      <Footer />
    </div>
  );
}

export default App;
