// Contact.js

import React, { useState } from "react";
import emailjs from "emailjs-com";

function Contact() {
  const [formData, setFormData] = useState({
    nom: "",
    prenom: "",
    email: "",
    entreprise: "",
    objet: "",
    message: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Vérification des champs obligatoires
    if (!formData.nom || !formData.email || !formData.message) {
      alert("Veuillez remplir les champs obligatoires: Nom, Email et Message");
      return;
    }

    // Envoi de l'e-mail via EmailJS
    emailjs.send('service_alokbam', 'YOUR_TEMPLATE_ID', formData, 'YOUR_USER_ID')
      .then((result) => {
        console.log(result.text);
        alert("Message envoyé avec succès!");
        // Réinitialiser le formulaire après l'envoi
        setFormData({
          nom: "",
          prenom: "",
          email: "",
          entreprise: "",
          objet: "",
          message: ""
        });
      }, (error) => {
        console.log(error.text);
        alert("Une erreur s'est produite, veuillez réessayer plus tard.");
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div>
      <h2>Contactez-nous</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom <span>*</span>:
          <input type="text" name="nom" value={formData.nom} onChange={handleChange} required />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={formData.prenom} onChange={handleChange} />
        </label>
        <label>
          Email <span>*</span>:
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </label>
        <label>
          Entreprise:
          <input type="text" name="entreprise" value={formData.entreprise} onChange={handleChange} />
        </label>
        <label>
          Objet:
          <input type="text" name="objet" value={formData.objet} onChange={handleChange} />
        </label>
        <label>
          Message <span>*</span>:
          <textarea name="message" value={formData.message} onChange={handleChange} required />
        </label>
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default Contact;
